#PASAR ºF a ºF
def cen_to_fah(centig:int):
    result = (centig*9/5)+32
    game.show_long_text("RESULT: "+Math.round_with_precision(result, 2)+ "ºF", DialogLayout.TOP)

#PASAR ºF a ºC 
def fah_to_cen(fahre:int):
    result = (fahre-32)*5/9
    game.show_long_text("RESULT: "+Math.round_with_precision(result, 2)+ "ºC", DialogLayout.TOP)

